import { registerMicroApps, runAfterFirstMounted, setDefaultMountApp, start } from 'qiankun';
import Vue from 'vue/dist/vue.esm';
const PortalApp = function () {
    const subApps = [
        {
            name: 'react16',
            entry: '//localhost:7100',
            render,
            activeRule: activeRule('/react16'),
        },
        {
            name: 'react15',
            entry: '//localhost:7102',
            render,
            activeRule: activeRule('/react15'),
        },
        {
            name: 'vue',
            entry: '//localhost:7101',
            render,
            activeRule: activeRule('/vue'),
        },
        {
            name: 'angular9',
            entry: '//localhost:7103',
            render,
            activeRule: activeRule('/angular9'),
        },
        {
            name: 'myAngular',
            entry: '//localhost:4200',
            render,
            activeRule: activeRule('/myAngular'),
        },
        {
            name: 'purehtml',
            entry: '//localhost:7104',
            render,
            activeRule: activeRule('/purehtml'),
        }
    ];
    const startOptions = {
        prefetch: true,
        jsSandbox: true,
        singular: false,
    };
    const lifeCycles = {
        beforeLoad: [app => beforeLoad(app)],
        beforeMount: [app => beforeMount(app)],
        afterMount: [app => afterMount(app)],
        beforeUnmount: [app => beforeUnmount(app)],
        afterUnmount: [app => afterUnmount(app)]
    };
    let app = null;
    /**
     * 获得Vue渲染器
     */
    function getVueRender({ appContent, loading }) {
        return new Vue({
            template: `
            <div id="subapp-container">
              <h4 v-if="loading" class="subapp-loading">Loading...</h4>
              <div v-html="appContent" />
            </div>
          `,
            el: '#subapp-container',
            data() {
                return {
                    appContent,
                    loading,
                };
            },
        });
    }
    /**
     * 渲染器
     */
    function render({ appContent, loading }) {
        if (!app) {
            app = getVueRender({ appContent, loading });
        } else {
            app.appContent = appContent;
            app.loading = loading;
        }
    }
    /**
     * 路由监听
     */
    function activeRule(routerPrefix) {
        return location => location.pathname.startsWith(routerPrefix);
    }
    /**
     * 加载前回调
     */
    function beforeLoad(app) {
        console.log("加载前回调", app);
    }
    /**
     * 挂载前回调
     */
    function beforeMount(app) {
        console.log("挂载前回调", app);
    }
    /**
     * 挂载后回调
     */
    function afterMount(app) {
        console.log("挂载后回调", app);
    }
    /**
     * 卸载前回调
     */
    function beforeUnmount(app) {
        console.log("卸载前回调", app);
    }
    /**
     * 卸载后回调
     */
    function afterUnmount(app) {
        console.log("卸载后回调", app);
    }
    /**
     * 第一次挂载完毕
     */
    function onFirstMounted() {
        console.log('第一个App挂载完毕');
    }
    function Init() {
        registerMicroApps(subApps, lifeCycles);
        runAfterFirstMounted(() => onFirstMounted());
        setDefaultMountApp('/');
        start(startOptions);
    }
    return {
        Init
    };
};
export default PortalApp;
