import { VueRender } from './VueRender';
import { registerMicroApps, runAfterFirstMounted, setDefaultMountApp, start, RegistrableApp, LifeCycles } from 'qiankun';

export class PortalApp {
    private subApps: Array<RegistrableApp<any>> = [
        {
            name: 'react16',
            entry: '//localhost:7100',
            render: VueRender.render,
            activeRule: this.activeRule('/react16'),
        },
        {
            name: 'react15',
            entry: '//localhost:7102',
            render: VueRender.render,
            activeRule: this.activeRule('/react15'),
        },
        {
            name: 'vue',
            entry: '//localhost:7101',
            render: VueRender.render,
            activeRule: this.activeRule('/vue'),
        },
        {
            name: 'angular9',
            entry: '//localhost:7103',
            render: VueRender.render,
            activeRule: this.activeRule('/angular9'),
        },
        {
            name: 'myAngular',
            entry: '//localhost:4200',
            render: VueRender.render,
            activeRule: this.activeRule('/myAngular'),
        },
        {
            name: 'purehtml',
            entry: '//localhost:7104',
            render: VueRender.render,
            activeRule: this.activeRule('/purehtml'),
        }
    ];
    private startOptions = {
        prefetch: true,
        jsSandbox: true,
        singular: false,
    };
    private lifeCycles: LifeCycles<any> = {
        beforeLoad: [app =>
            new Promise<any>(() => {
                this.beforeLoad(app)
            })],
        beforeMount: [app => 
            new Promise<any>(() => {
                this.beforeMount(app)
            })],
        afterMount: [app => 
            new Promise<any>(() => {
                this.afterMount(app)
            })],
        beforeUnmount: [app => 
            new Promise<any>(() => {
                this.beforeUnmount(app)
            })],
        afterUnmount: [app => 
            new Promise<any>(() => {
                this.afterUnmount(app)
            })]
    };
    /**
     * 路由监听
     */
    private activeRule(routerPrefix: string) {
        return (location.pathname as any).startsWith(routerPrefix);
    }
    /**
     * 加载前回调
     */
    private beforeLoad(app: RegistrableApp<any>): void {
        console.log("加载前回调", app);
    }
    /**
     * 挂载前回调
     */
    private beforeMount(app: RegistrableApp<any>): void {
        console.log("挂载前回调", app);
    }
    /**
     * 挂载后回调
     */
    private afterMount(app: RegistrableApp<any>): void {
        console.log("挂载后回调", app);
    }
    /**
     * 卸载前回调
     */
    private beforeUnmount(app: RegistrableApp<any>): void {
        console.log("卸载前回调", app);
    }
    /**
     * 卸载后回调
     */
    private afterUnmount(app: RegistrableApp<any>): void {
        console.log("卸载后回调", app);
    }
    /**
     * 第一次挂载完毕
     */
    private onFirstMounted() {
        console.log('第一个App挂载完毕');
    }
    /**
     * 初始化
     */
    public Init() {
        console.log(this.subApps, this.lifeCycles);
        registerMicroApps(this.subApps, this.lifeCycles);
        runAfterFirstMounted(() => this.onFirstMounted());
        setDefaultMountApp('/');
        start(this.startOptions);
    }
}
